package JavaBasics;

public class Nestedifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=8;
		int c=3;
		if(a>b)
		{
			if(c<a)
			{
			System.out.println("block 1");
			}
			else
				
			{
				System.out.println("block 2");
			}
			
			
		}
		else
		{
			if(a>c)
			{
				System.out.println("block 3");
			}
			else
			{
				System.out.println("block 4");
			}
			
			}
			
			
			
			
		}
			
			
			
	}


