package JavaBasics;

public class Switchcaseprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		
		
		switch(1)
		{
		case 1: System.out.println("Statement1");
		       break;
		case 2:System.out.println("Ststement2") ;
		       break;
		case 3: System.out.println("Statement3");
		       break;
		default:System.out.println("out of switchstatement");
		}
	}
}